  END IF;
  IF NOT val ? 'geometry' THEN
    RETURN FALSE;
  END IF;
  pval := val -> 'geometry';
  -- .'^'.2.geometry
  res := JSONB_TYPEOF(pval) = 'object';
  IF res THEN
    IF pval ? 'type' THEN
      tag_2 := pval -> 'type';
      fun_2 := jm_cmap_get('_jm_map_2', tag_2);
      IF fun_2 IS NOT NULL THEN
        res := jm_call(fun_2, pval, path, rep);
      ELSE
        res := FALSE;
      END IF;
    ELSE
      res := FALSE;
    END IF;
  END IF;
  IF NOT res THEN
    -- .'^'.2.geometry.'|'
    res := JSONB_TYPEOF(pval) = 'null';
  END IF;
  IF NOT res THEN
    RETURN FALSE;
  END IF;
  IF NOT val ? 'properties' THEN
    RETURN FALSE;
  END IF;
  pval := val -> 'properties';
  -- .'^'.2.properties
  -- .'^'.2.properties.'|'.0
  res := JSONB_TYPEOF(pval) = 'null';
  IF NOT res THEN
    -- .'^'.2.properties.'|'.1
    res := _jm_obj_32(pval, path, rep);
  END IF;
  IF NOT res THEN
    RETURN FALSE;
  END IF;
  IF val ? 'id' THEN
    pval := val -> 'id';
    -- .'^'.2.id
    -- .'^'.2.id.'|'.0
    res := JSONB_TYPEOF(pval) = 'string';
    IF NOT res THEN
      -- .'^'.2.id.'|'.1
      res := JSONB_TYPEOF(pval) = 'number';
    END IF;
    IF NOT res THEN
      RETURN FALSE;
    END IF;
  END IF;
  IF val ? 'bbox' THEN
    pval := val -> 'bbox';
    -- .'^'.2.bbox
    res := JSONB_TYPEOF(pval) = 'array';
    IF res THEN
      FOR arr_58_idx IN 0 .. JSONB_ARRAY_LENGTH(pval) - 1 LOOP
        arr_58_item := pval -> arr_58_idx;
        -- .'^'.2.bbox.0
        res := JSONB_TYPEOF(arr_58_item) = 'number';
        IF NOT res THEN
          EXIT;
        END IF;
      END LOOP;
    END IF;
    IF NOT res THEN
      RETURN FALSE;
    END IF;
  END IF;
  RETURN TRUE;
END;
$$ LANGUAGE PLpgSQL;

