  pval := val -> 'c';
  -- .'$r'.c
  res := JSONB_TYPEOF(pval) = 'string' AND JSON_VALUE(pval, '$' RETURNING TEXT) = 'cst_01';
  IF NOT res THEN
    RETURN FALSE;
  END IF;
  IF NOT val ? 'd' THEN
    RETURN FALSE;
  END IF;
  pval := val -> 'd';
  -- .'$r'.d
  RETURN JSONB_TYPEOF(pval) = 'string' AND JSON_VALUE(pval, '$' RETURNING TEXT) = 'cst_01';
END;
$$ LANGUAGE PLpgSQL;

