            # .'$Xx'.'|'.2
            $res = jm_is_integer($val) && $val >= 1;
            if (! $res)
            {
                # .'$Xx'.'|'.3
                $res = jm_is_numeric($val) && $val > 0.0;
            }
        }
    }
    return $res;
}

