# check $ObjectSchema (.'$ObjectSchema')
sub json_model_15($$$)
{
    my ($val, $path, $rep) = @_;
    # .'$ObjectSchema'
    if (! jm_is_object($val))
    {
        return 0;
    }
    my $pfun;
    scalar keys %$val;
    while (my ($prop, $pval) = each %$val)
    {
        if (($pfun = $json_model_15_map{$prop}))
        {
            # handle 57 may props
            if (defined($pfun) && ! &$pfun($pval, undef, undef))
            {
                return 0;
            }
            next;
        }
        return 0;
    }
    return 1;
}

